const tabLinks=document.querySelectorAll(".info__nav-button"),wrapper=document.querySelector(".info__list"),ACTIVE_LINK_CLASS="info__nav-button--active";let activeLink=0;tabLinks[activeLink].classList.add(ACTIVE_LINK_CLASS);for(let t=0;t<tabLinks.length;t++){tabLinks[t].addEventListener("click",setClickedTab)}function setClickedTab(t){removeActive();const e=t.target;activeLink=e.dataset.id,changePosition(e)}function changePosition(t){t.classList.add(ACTIVE_LINK_CLASS),wrapper.style.left=-100*(t.getAttribute("data-id")-1)+"%"}function removeActive(){for(let t=0;t<tabLinks.length;t++)tabLinks[t].classList.remove(ACTIVE_LINK_CLASS)}