const logbookInfo=()=>{const o=document.querySelector(".js-logbookInfo"),e=document.querySelectorAll(".js-openLogbookInfo"),n=o.querySelector(".js-closeLogbookInfo"),s="logbook__info--show";e.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),o.classList.add(s),n.addEventListener("click",()=>{o.classList.remove(s)}),window.addEventListener("keydown",e=>{27===e.keyCode&&o.classList.contains(s)&&(e.preventDefault(),o.classList.remove(s))})})})};logbookInfo();