const timepicker=()=>{const e=document.querySelectorAll(".js-openTimepicker");e.forEach(e=>{const s=e.nextElementSibling,i=s.querySelector(".js-timeTimepicker"),o=s.querySelector(".js-dateTimepicker"),l=s.nextElementSibling,d=new Datepicker(o,{language:"ru",format:"dd.mm.yy",todayHighlight:!0,allowOneSidedRange:!0,autohide:!0});e.addEventListener("click",e=>{e.preventDefault(),s.classList.toggle("fieldset__dropdown--show"),l.classList.toggle("fieldset__blackout--show")}),i.addEventListener("change",s=>{t(e,i.value,d.getDate("dd.mm.yyyy"))}),o.addEventListener("changeDate",s=>{t(e,i.value,d.getDate("dd.mm.yyyy"))}),l.addEventListener("click",()=>{s.classList.toggle("fieldset__dropdown--show"),l.classList.toggle("fieldset__blackout--show")}),window.addEventListener("keydown",e=>{27===e.keyCode&&s.classList.contains("fieldset__dropdown--show")&&(e.preventDefault(),s.classList.remove("fieldset__dropdown--show"),l.classList.remove("fieldset__blackout--show"))})});const t=(e,t,s)=>{e.value=`${t} - ${s}`}};timepicker();